import matplotlib.pyplot as plt
import numpy as np

def show_grey_image(image_array, width, height, save_path):
    # 将一维数组转换为二维数组
    image_2d = np.reshape(image_array, (height, width))
    plt.imshow(image_2d, cmap='gray')
    plt.axis('off')  # 关闭坐标轴
    plt.savefig(save_path, bbox_inches='tight', pad_inches=0)


